    $("#tabtab td").css("background-color","rgb(233, 233, 233)");
    $("#tabtab td:eq("+$id+")").css("background-color","rgb(0, 99, 180)");
    $("#tabtab td font").css("color","#333333");
    $("#tabtab td:eq("+$id+") font").css("color","rgb(233, 233, 233)");
    //选项卡内容
    $("#tableLie .tabli:eq("+$id+")").css("display","block");
}
</script>

